using System.Collections;using System.Collections.Generic;using UnityEngine;public class Enemy : MonoBehaviour {    private int hp;    public Enemy(int hp)    {        this.hp = hp;    }    public int getHp()    {        return hp;    }    public void reciveDamage(int damage)    {        hp -= damage;    }    public void vanish()    {        Destroy(gameObject);    }    public bool deadOrLive()    {        if (hp > 0)        {            return true;        }        return false;    }}